    // LineMatchActivity ì»´í¬ë„ŒíŠ¸
    function LineMatchActivity\({pairs, onComplete}\) {
      const canvasRef = useRef\(null\);
      const [lines, setLines] = useState\([]\);
      const [currentLine, setCurrentLine] = useState\(null\);

      useEffect\(\(\) => {
        if \(lines.length === pairs.length\) {
          const timer = setTimeout\(\(\) => onComplete\(\), 500\);
          return \(\) => clearTimeout\(timer\);
        }
      }, [lines, pairs.length, onComplete]\);

      useEffect\(\(\) => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext\('2d'\);
        ctx.clearRect\(0, 0, canvas.width, canvas.height\);

        lines.forEach\(line => {
          ctx.strokeStyle = '#667eea';
          ctx.lineWidth = 3;
          ctx.beginPath\(\);
          ctx.moveTo\(line.x1, line.y1\);
          ctx.lineTo\(line.x2, line.y2\);
          ctx.stroke\(\);
        }\);

        if \(currentLine\) {
          ctx.strokeStyle = '#999';
          ctx.lineWidth = 2;
          ctx.setLineDash\([5, 5]\);
          ctx.beginPath\(\);
          ctx.moveTo\(currentLine.x1, currentLine.y1\);
          ctx.lineTo\(currentLine.x2, currentLine.y2\);
          ctx.stroke\(\);
          ctx.setLineDash\([]\);
        }
      }, [lines, currentLine]\);

      const handleLeftClick = \(e, pair\) => {
        const rect = e.currentTarget.getBoundingClientRect\(\);
        const x1 = rect.right;
        const y1 = rect.top + rect.height / 2;
        setCurrentLine\({id: pair.id, x1, y1, x2: x1, y2: y1}\);
      };

      const handleRightEnter = \(e, pair\) => {
        if \(currentLine\) {
          const rect = e.currentTarget.getBoundingClientRect\(\);
          const x2 = rect.left;
          const y2 = rect.top + rect.height / 2;
          setCurrentLine\(prev => \({...prev, x2, y2}\)\);
        }
      };

      const handleRightClick = \(e, pair\) => {
        if \(currentLine && currentLine.id === pair.id\) {
          const rect = e.currentTarget.getBoundingClientRect\(\);
          const x2 = rect.left;
          const y2 = rect.top + rect.height / 2;
          setLines\(prev => [...prev, {...currentLine, x2, y2}]\);
          setCurrentLine\(null\);
        }
      };

      const handleMouseMove = \(e\) => {
        if \(currentLine\) {
          const canvas = canvasRef.current;
          const rect = canvas.getBoundingClientRect\(\);
          const x2 = e.clientX - rect.left;
          const y2 = e.clientY - rect.top;
          setCurrentLine\(prev => \({...prev, x2, y2}\)\);
        }
      };

      return \(
        <div style={{
          height: '100%',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          padding: '40px',
          background: '#f8f9fa',
          position: 'relative'
        }}>
          <h2 style={{marginBottom: '40px', fontSize: '32px', color: '#333'}}>ì•Œë§ì€ ê²ƒë¼ë¦¬ ì„ ìœ¼ë¡œ ì—°ê²°í•˜ì„¸ìš”</h2>
          <div style={{position: 'relative', width: '100%', maxWidth: '800px'}}>
            <canvas
              ref={canvasRef}
              width={800}
              height={400}
              onMouseMove={handleMouseMove}
              style={{position: 'absolute', top: 0, left: 0, pointerEvents: currentLine ? 'auto' : 'none'}}
            />
            <div style={{display: 'flex', justifyContent: 'space-between', position: 'relative', zIndex: 1}}>
              <div style={{display: 'flex', flexDirection: 'column', gap: '40px', padding: '20px'}}>
                {pairs.map\(pair => \(
                  <div
                    key={pair.id}
                    onClick={\(e\) => handleLeftClick\(e, pair\)}
                    style={{
                      padding: '20px 40px',
                      background: '#667eea',
                      color: 'white',
                      borderRadius: '12px',
                      cursor: 'pointer',
                      fontSize: '24px',
                      fontWeight: 'bold',
                      boxShadow: '0 4px 6px rgba\(0,0,0,0.1\)'
                    }}
                  >
                    {pair.left}
                  </div>
                \)\)}
              </div>
              <div style={{display: 'flex', flexDirection: 'column', gap: '40px', padding: '20px'}}>
                {pairs.map\(pair => \(
                  <div
                    key={pair.id}
                    onMouseEnter={\(e\) => handleRightEnter\(e, pair\)}
                    onClick={\(e\) => handleRightClick\(e, pair\)}
                    style={{
                      padding: '20px 40px',
                      background: '#764ba2',
                      color: 'white',
                      borderRadius: '12px',
                      cursor: 'pointer',
                      fontSize: '24px',
                      fontWeight: 'bold',
                      boxShadow: '0 4px 6px rgba\(0,0,0,0.1\)'
                    }}
                  >
                    {pair.right}
                  </div>
                \)\)}
              </div>
            </div>
          </div>
        </div>
      \);
    }

    // CameraModule ì»´í¬ë„ŒíŠ¸
    function CameraModule\({onComplete}\) {
      const videoRef = useRef\(null\);
      const canvasRef = useRef\(null\);
      const [stream, setStream] = useState\(null\);
      const [photo, setPhoto] = useState\(null\);
      const [error, setError] = useState\(null\);

      useEffect\(\(\) => {
        startCamera\(\);
        return \(\) => {
          if \(stream\) {
            stream.getTracks\(\).forEach\(track => track.stop\(\)\);
          }
        };
      }, []\);

      const startCamera = async \(\) => {
        try {
          const mediaStream = await navigator.mediaDevices.getUserMedia\({video: {facingMode: 'user'}, audio: false}\);
          setStream\(mediaStream\);
          if \(videoRef.current\) {
            videoRef.current.srcObject = mediaStream;
          }
        } catch \(err\) {
          setError\('ì¹´ë©”ë¼ì— ì ‘ê·¼í•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤.'\);
          console.error\(err\);
        }
      };

      const capturePhoto = \(\) => {
        const video = videoRef.current;
        const canvas = canvasRef.current;
        const ctx = canvas.getContext\('2d'\);

        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        ctx.drawImage\(video, 0, 0\);

        const imageData = canvas.toDataURL\('image/png'\);
        setPhoto\(imageData\);
      };

      const retake = \(\) => {
        setPhoto\(null\);
      };

      const confirmPhoto = \(\) => {
        if \(stream\) {
          stream.getTracks\(\).forEach\(track => track.stop\(\)\);
        }
        onComplete\(\);
      };

      if \(error\) {
        return \(
          <div style={{
            height: '100%',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            background: '#f8f9fa'
          }}>
            <p style={{fontSize: '24px', color: '#666'}}>{error}</p>
          </div>
        \);
      }

      return \(
        <div style={{
          height: '100%',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          padding: '40px',
          background: '#f8f9fa'
        }}>
          <h2 style={{marginBottom: '20px', fontSize: '32px', color: '#333'}}>ì‚¬ì§„ì„ ì´¬ì˜í•˜ì„¸ìš”</h2>
          {!photo ? \(
            <>
              <video
                ref={videoRef}
                autoPlay
                playsInline
                style={{
                  maxWidth: '100%',
                  maxHeight: '60vh',
                  borderRadius: '12px',
                  boxShadow: '0 4px 6px rgba\(0,0,0,0.1\)',
                  background: '#000'
                }}
              />
              <button
                onClick={capturePhoto}
                style={{
                  marginTop: '20px',
                  padding: '12px 32px',
                  fontSize: '18px',
                  borderRadius: '8px',
                  border: 'none',
                  background: 'linear-gradient\(135deg, #667eea 0%, #764ba2 100%\)',
                  color: 'white',
                  cursor: 'pointer',
                  fontWeight: 'bold'
                }}
              >
                ğŸ“· ì´¬ì˜
              </button>
            </>
          \) : \(
            <>
              <img
                src={photo}
                alt="ì´¬ì˜ëœ ì‚¬ì§„"
                style={{
                  maxWidth: '100%',
                  maxHeight: '60vh',
                  borderRadius: '12px',
                  boxShadow: '0 4px 6px rgba\(0,0,0,0.1\)'
                }}
              />
              <div style={{marginTop: '20px', display: 'flex', gap: '10px'}}>
                <button
                  onClick={retake}
                  style={{
                    padding: '12px 32px',
                    fontSize: '18px',
                    borderRadius: '8px',
                    border: '2px solid #667eea',
                    background: 'white',
                    color: '#667eea',
                    cursor: 'pointer',
                    fontWeight: 'bold'
                  }}
                >
                  ë‹¤ì‹œ ì°ê¸°
                </button>
                <button
                  onClick={confirmPhoto}
                  style={{
                    padding: '12px 32px',
                    fontSize: '18px',
                    borderRadius: '8px',
                    border: 'none',
                    background: 'linear-gradient\(135deg, #667eea 0%, #764ba2 100%\)',
                    color: 'white',
                    cursor: 'pointer',
                    fontWeight: 'bold'
                  }}
                >
                  í™•ì¸
                </button>
              </div>
            </>
          \)}
          <canvas ref={canvasRef} style={{display: 'none'}} />
        </div>
      \);
    }

    // InteractiveViewer ì»´í¬ë„ŒíŠ¸
    function InteractiveViewer\(\) {
      const [currentPage, setCurrentPage] = useState\(0\);
      const [activityStates, setActivityStates] = useState\({}\);

      const page = DEMO_PAGES[currentPage];

      const handleComplete = \(\) => {
        setActivityStates\(prev => \({...prev, [currentPage]: {completed: true}}\)\);
      };

      const handleReset = \(\) => {
        setActivityStates\(prev => \({...prev, [currentPage]: {completed: false}}\)\);
      };

      const handleNext = \(\) => {
        if \(currentPage < DEMO_PAGES.length - 1\) {
          setCurrentPage\(currentPage + 1\);
        }
      };

      const handlePrev = \(\) => {
        if \(currentPage > 0\) {
          setCurrentPage\(currentPage - 1\);
        }
      };

      const isCompleted = activityStates[currentPage]?.completed;
      const isLastPage = currentPage === DEMO_PAGES.length - 1;

      return \(
        <div className="viewer-container">
          <div className="toolbar">
            <div className="toolbar-section">
              <button onClick={handlePrev} disabled={currentPage === 0}>ì´ì „</button>
              <span style={{fontSize: '16px', fontWeight: 'bold'}}>
                {currentPage + 1} / {DEMO_PAGES.length} - {page.title}
              </span>
              <button onClick={handleNext} disabled={currentPage === DEMO_PAGES.length - 1}>ë‹¤ìŒ</button>
            </div>
            <div className="toolbar-section">
              <button onClick={\(\) => setCurrentPage\(0\)}>ì²˜ìŒìœ¼ë¡œ</button>
            </div>
          </div>
          <div className="page-content">
            {page.type === 'intro' && <IntroPage />}
            {page.type === 'dragdrop' && <DragDropActivity pairs={page.pairs} onComplete={handleComplete} />}
            {page.type === 'coloring' && <ColoringActivity onComplete={handleComplete} />}
            {page.type === 'linematch' && <LineMatchActivity pairs={page.pairs} onComplete={handleComplete} />}
            {page.type === 'camera' && <CameraModule onComplete={handleComplete} />}
            {isCompleted && <CompletionOverlay onNext={handleNext} onReset={handleReset} isLastPage={isLastPage} />}
          </div>
        </div>
      \);
    }

    ReactDOM.createRoot\(document.getElementById\('root'\)\).render\(<InteractiveViewer />\);
  </script>
